package conditionals;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sayi=19;
		if (sayi<20) {
			System.out.println("sayı 20'den küçük");
		}
		else if (sayi==20) {
			System.out.println("sayı 20ye eşittir");
		} else {
			System.out.println("sayı 20den büyüktür");

		}
	}

}
